import React, { Component } from 'react';
import axios from 'axios';

export default class Ajout extends Component {
  constructor(props) {
    super(props);
    this.onChangePersonName = this.onChangePersonName.bind(this);
    this.onChangeBusinessName = this.onChangeBusinessName.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      nom: '',
      prenom: ''
    }
  }
  onChangePersonName(e) {
    this.setState({
      person_name: e.target.value
    });
  }
  onChangeBusinessName(e) {
    this.setState({
      nom: e.target.value
    })  
  }
  onSubmit(e) {
    e.preventDefault();
    let formData = {name: 'Alex', surname: 'Moran'};
    const encodeForm = (data) => {
      return Object.keys(data)
          .map(key => encodeURIComponent(key) + '=' + encodeURIComponent(data[key]))
          .join('&');
    }
    axios.post('http://localhost:8080/list', encodeForm(formData), {headers: {'Accept': 'application/json'}})
    .then(function (response) {
        console.log(response);
    })
    .catch(function (error) {
        console.log(error);
    });
    // const obj = {
    //   nom: this.state.person_name,
    //   prenom: this.state.business_name
    // };
    // axios.post('http://localhost:8080/list', {id : 6, name: 'Alex', surname: 'Moran'})
    //     .then(res => console.log(res.data));

    // this.setState({
    //   nom: '',
    //   prenom: ''
    // })
  }

  render() {
    return (
        <div style={{ marginTop: 10 }}>
            <h3>Add New Business</h3>
            <form onSubmit={this.onSubmit}>
                <div className="form-group">
                    <label>Person Name:  </label>
                    <input 
                      type="text" 
                      className="form-control" 
                      value={this.state.person_name}
                      onChange={this.onChangePersonName}
                      />
                </div>
                <div className="form-group">
                    <label>Business Name: </label>
                    <input type="text" 
                      className="form-control"
                      value={this.state.business_name}
                      onChange={this.onChangeBusinessName}
                      />
                </div>
                <div className="form-group">
                    <input type="submit" value="Register Business" className="btn btn-primary"/>
                </div>
            </form>
        </div>
    )
  }
}
