import React, { Component } from 'react';
import axios from 'axios';

export default class Create extends Component {
  constructor(props) {
      super(props);
      this.onChangePersonName = this.onChangePersonName.bind(this);
      this.onChangeBusinessName = this.onChangeBusinessName.bind(this);
      this.onSubmit = this.onSubmit.bind(this);

      this.state = {
          id : '',
          person_name: '',
          business_name: '',
          list: ''
      }
  }
  onChangePersonName(e) {
    this.setState({
      person_name: e.target.value
    });
  }
  onChangeBusinessName(e) {
    this.setState({
      business_name: e.target.value
    })  
  }


  // Fetch the list on first mount
  componentDidMount() {
    this.getList();
  }

  // Retrieves the list of items from the Express app
  getList = () => {
  }

  onSubmit(e) {

    fetch('http://localhost:3000/list')
    .then(res => res.text())
    .then(list => this.setState({ list:list }))
    e.preventDefault();

    var test = this.state.list
    //console.log( JSON.parse(test))
    var test1 = JSON.parse(test)
    var compte = 0;

    if (test1.length === 0 ){
      compte = -1

    }else{

      for (let i = 0; i < test1.length; i++) {
        if(compte < test1[i].id )
          compte = test1[i].id
      }

    }
    console.log("coucou", test1.length);

    const obj = {
      id: compte + 1,
      nom: this.state.person_name,
      prenom: this.state.business_name
    };
    axios.post('http://localhost:8080/list', obj)
        .then(res => console.log(res.data));

    this.setState({
      person_name: '',
      business_name: ''
    })
  }

  render() {
      return (
          <div style={{ marginTop: 10 }}>
              <form onSubmit={this.onSubmit} action="post">
                  <div className="form-group">
                      <label>Nom:  </label>
                      <input 
                        type="text" 
                        className="form-control" 
                        value={this.state.person_name}
                        onChange={this.onChangePersonName}
                        />
                  </div>
                  <div className="form-group">
                      <label>Prénom: </label>
                      <input type="text" 
                        className="form-control"
                        value={this.state.business_name}
                        onChange={this.onChangeBusinessName}
                        />
                  </div>
                  <div className="form-group">
                      <input type="submit" value="Ajouter" className="btn btn-primary"/>
                  </div>
              </form>
          </div>
      )
  }
}
